<template>
  <q-card flat bordered class="qr-code-scanner">
    <q-card-section class="text-center">
      <img
        class="qr-icon"
        src="data:image/png;base64,"
        alt=""
      />
      <base-input
        v-model="qrcode"
        type="text"
        ref="qrScanner"
        debounce="1000"
        class="scanner"
        @update:model-value="onScanSuccess"
      />
      <div class="scanner-label">{{ $t("connectYourBarcodeScanner") }}</div>
      <base-btn
        v-if="isScanning"
        color="negative"
        icon="fas fa-barcode-read"
        :label="$t('stopScanning')"
        @click="stopScan"
        outline
      />
      <base-btn
        v-else
        color="primary"
        icon="fas fa-barcode-read"
        :label="$t('startScanning')"
        @click="startScan"
        outline
      />
    </q-card-section>
  </q-card>
</template>

<script>
export default {
  data() {
    return {
      qrcode: null,
      isScanning: false,
    };
  },

  emits: ["detect"],

  methods: {
    stopScan() {
      this.isScanning = false;
      document.removeEventListener("keypress", this.onInput);
      document.removeEventListener("keydown", this.onInput);
      this.$refs.qrScanner.$el.blur();
    },
    onScanSuccess(val) {
      if (val) {
        this.$emit("detect", val, "scanner");
        this.qrcode = null;
      }
    },
    onInput(event) {
      this.$refs.qrScanner.$el.focus();
    },
    startScan() {
      document.addEventListener("keypress", this.onInput);
      document.addEventListener("keydown", this.onInput);
      this.isScanning = true;
    },
  },

  beforeUnmount() {
    this.stopScan();
  },
};
</script>
<style lang="scss">
.qr-code-scanner {
  position: relative;
  .scanner-label {
    position: absolute;
    text-align: center;
    left: 0;
    right: 0;
    bottom: 60px;
    z-index: 1;
  }
  .qr-icon {
    width: 70px;
  }
  .scanner {
    position: relative;
    &:before {
      content: "";
      position: absolute;
      left: 0;
      right: 0;
      top: 0;
      bottom: 0;
      background: white;
      padding-top: 10px;
      z-index: 1;
    }
  }
}
</style>
